import React from 'react';
import Result1 from './result_1';
import styles from './index.module.css';

function Result(props) {
    const resultHash = {
        'Best':{'D':0, 'I':0, 'S':0, 'C':0, 'N':0},
        'Wrst':{'D':0, 'I':0, 'S':0, 'C':0, 'N':0},
        'Result':{'D':0, 'I':0, 'S':0, 'C':0}
    };

	const [rType, setRType] = React.useState([]);
	const [calControl, setCalControl] = React.useState(false);

    if (props.status === 2) {
        props.befCalAnswer.forEach((item, idx) => {
            props.questions[idx].forEach((jtem, jdx) => {
                if (jdx === item[0][0]) {
					if (jtem[1] === 'D') resultHash.Best.D += 1;
					if (jtem[1] === 'I') resultHash.Best.I += 1;
					if (jtem[1] === 'S') resultHash.Best.S += 1;
					if (jtem[1] === 'C') resultHash.Best.C += 1;
					if (jtem[1] === 'N') resultHash.Best.N += 1;
				}

                if (jdx === item[0][1]) {
					if (jtem[2] === 'D') resultHash.Wrst.D += 1;
					if (jtem[2] === 'I') resultHash.Wrst.I += 1;
					if (jtem[2] === 'S') resultHash.Wrst.S += 1;
					if (jtem[2] === 'C') resultHash.Wrst.C += 1;
					if (jtem[3] === 'N') resultHash.Wrst.N += 1;
				}
            })
        });

		resultHash.Result.D = resultHash.Best.D - resultHash.Wrst.D;
		resultHash.Result.I = resultHash.Best.I - resultHash.Wrst.I;
		resultHash.Result.S = resultHash.Best.S - resultHash.Wrst.S;
		resultHash.Result.C = resultHash.Best.C - resultHash.Wrst.C;
		
		// Calculate DISC Score
		const division = [
			[[27, 6], [5, 0], [-1, -4], [-5, -7], [-8, -11], [-12, -14], [-15, -27]],
			[[28, 8], [7, 6], [5, 3], [2, 1], [0, -2], [-3, -5], [-6, -26]],
			[[26, 12], [11, 9], [8, 6], [5, 3], [2, 0], [-1, -4], [-5, -27]],
			[[24, 16], [5, 3], [2, 0], [-1, -2], [-3, -5], [-6, -8], [-9, -26]]
		]; const score = [];
		division.forEach((item, idx) => {
			item.forEach((jtem, jdx) => {
				if (idx === 0 && (jtem[1] <= resultHash.Result.D && resultHash.Result.D <= jtem[0])) score.push(['D', jdx + 1]);
				if (idx === 1 && (jtem[1] <= resultHash.Result.I && resultHash.Result.I <= jtem[0])) score.push(['I', jdx + 1]);
				if (idx === 2 && (jtem[1] <= resultHash.Result.S && resultHash.Result.S <= jtem[0])) score.push(['S', jdx + 1]);
				if (idx === 3 && (jtem[1] <= resultHash.Result.C && resultHash.Result.C <= jtem[0])) score.push(['C', jdx + 1]);
			})
		})

		score.sort((a, b) => {return b[1] - a[1]});
		setRType(score);
		console.log(score);
    }

    return (
        <>
        <div className={props.status === 2 ? styles.result_wrap_show : styles.result_wrap_hide}>
			<Result1 rType={rType}/>
        </div>
        </>
    );
}

export default Result;
